mapper.update:
update t_mail
set title = #{title,jdbcType=VARCHAR},
  send_time = #{sendTime,jdbcType=TIMESTAMP},
  body = #{body,jdbcType=LONGVARCHAR}
where mail_id = #{mailId,jdbcType=BIGINT}

update t_mail
set title = #{title,jdbcType=VARCHAR},
  send_time = #{sendTime,jdbcType=TIMESTAMP}
where mail_id = #{mailId,jdbcType=BIGINT}

mapper.select:
select m.mail_id as m_id, m.title as m_title, m.body as m_body, m.send_time as m_send_time , u1.`name` as 'u_sender', u2.`name` as 'u_receiver' from t_mail m left join t_sender_mail sm on m.mail_id = sm.mail_id left join t_user u1 on sm.user_id = u1.user_id left join t_receiver_mail rm on rm.mail_id = m.mail_id left join t_user u2 on rm.user_id = u2.user_id
sm.state_id != 3
and m.title like #{title}
and u1.name = #{sender}
and u2.name = #{receiver}

select u.name
from t_receiver_mail r left join t_user u on r.user_id = u.user_id
where r.mail_id = #{mailId,jdbcType=BIGINT}
